package Receiver;import com.sonycsl.echo.Echo;public class MyEchoEventListener extends Echo.EventListener implements ResultControllable {      protected OnReceiveResult onItemSetChanging;      @Override      public void setOnReceive(OnReceiveResult onSetEPC, OnReceiveResult onItemSetChanging) {            this.onItemSetChanging = onItemSetChanging;      }      @Override      public OnReceiveResult getOnGetEPC() {            return onItemSetChanging;      }}