package Receiver;import android.app.Activity;import android.widget.Toast;public class ResultHandle {      private Runnable successHandle, failHandle;      private Activity activity;      public ResultHandle(Runnable successHandle, Runnable failHandle, Activity activity) {            this.successHandle = successHandle;            this.failHandle = failHandle;            this.activity = activity;      }      public void successRun() {            if (activity != null) {                  activity.runOnUiThread(() -> Toast.makeText(activity, "正常に設定た", Toast.LENGTH_SHORT).show());            }            successHandle.run();      }      public void failRun() {            if (activity != null) {                  activity.runOnUiThread(() -> Toast.makeText(activity, "設定に失敗た", Toast.LENGTH_SHORT).show());            }            failHandle.run();      }}